/** * Function: * * @author Huangqing * @date 2019/1/7 10:08 * @since JKD 1.8 */public class LeetCode9 {    /**     * 163ms     * @param x     * @return     */    public boolean isPalindrome(int x) {        if (x < 0) {            return false;        }        String s = String.valueOf(x);        int i = 0;        while (i < s.length() / 2) {            if (s.charAt(i) != s.charAt(s.length() - i - 1)) {                return false;            }            i++;        }        return true;    }    /**     * 76ms     * @param x     * @return     */    public boolean isPalindrome1(int x) {        if (x < 0) {            return false;        }        int rev = 0;        while (x > rev) {            rev = rev * 10 + x % 10;            x /= 10;        }        return (x == rev || x == rev / 10);    }    public static void main(String[] args) {        LeetCode9 s = new LeetCode9();        System.out.println(s.isPalindrome1(121));    }}